ee:message.ee:set-payload:
%dw 2.0
output application/xml
import formatcurrency from modules::Format

//Code assumes there are three payloads saved as variables:
//  -> flightsPayload
//  -> transactionsPayload
//  -> accountsPayload

//Create a function to take care of building the Transaction objects
//End result should be an array of Transaction objects with a new structure according to the Transaction datatype in the RAML spec

fun createNewTransactions(transactions) =
	transactions map ((transaction) ->

		using (account = (vars.accountsoutput filter ($.accountID == transaction.customerRef))[0])
		(
			 transaction @(transID: transaction.transactionID, amount: transaction.amount as Number):
				{
					idCustomer: transaction.customerRef,
					nameCustomer: account.name,
					accountType: account.accountType,
					region: transaction.region,

			    } 	++
			    formatcurrency(transaction.region,transaction.amount)    
		)
	)
---
flights:  
{(
	//Filter the transactions according to the unique Flight ID in vars.transactionsPayload

		//Build the rest of the transformations here for each flight object in vars.flightsPayload. Refer to the RAML spec on what exactly needs to get mapped

		vars.flights map((flightobject,index) ->
			using(localflightID = (upper(flightobject.airlineName) ++ upper(flightobject.flightCode) ++ flightobject.departureDate as Date {format:"yyyy/M/dd"} as String {formaat:"yyyy-MM-dd"}))
			using (filteredTransactions = (vars.transaction filter ($.flightID == localflightID )))
		    (

			    flight @(flightCode:flightobject.flightCode):
			    {
			    	airline: flightobject.airlineName,
			    	departureDate: flightobject.departureDate as Date {format: "yyyy/MM/dd"} as String {format :"yyyy-MMM-dd"},
			    	emptySeats:flightobject.availableSeats,
			   	    fromAirportCode: flightobject.origination,
			   	    planeType: flightobject.planeType,
			   	    price: flightobject.price as Number as String {format:"###.0"} ,
			    	toAirportCode: flightobject.destination,
					transactions:{(createNewTransactions(filteredTransactions))}
		        }
		    )	
		)		
)}

ee:variables.ee:set-variable:
%dw 2.0
output application/json

fun createNewTransactions(transactions) =
	transactions map ((transaction) ->

		//Assign a local variable within the map scope by utilizing the "using" operator
		using (account = (vars.accountsoutput filter ($.accountID == transaction.customerRef))[0])
		(	 
			{
				idCustomer: transaction.customerRef,
				transID: transaction.transactionID,
				amount: transaction.amount,
				nameCustomer: account.name,
				'type':account.accountType,
				flightid: transaction.flightID
			}
		)
	)
---
flights:  
	//Filter the transactions according to the unique Flight ID in vars.transactionsPayload

		vars.flights map(flightobject,index) ->
			using(localflightID = (upper(flightobject.airlineName) ++ upper(flightobject.flightCode) ++ flightobject.departureDate as Date {format:"yyyy/M/dd"} as String {formaat:"yyyy-MM-dd"}))
			using (filteredTransactions = (vars.transaction filter ($.flightID == localflightID )))
			    (
			    	{
			    airline: flightobject.airlineName,
			    departureDate: flightobject.departureDate,
			    emptySeats:flightobject.availableSeats,
			    fromAirportCode: flightobject.origination,
			    planeType: flightobject.planeType,
			    price: flightobject.price,
			    toAirportCode: flightobject.destination,
				transactions:createNewTransactions(filteredTransactions)

		}			

	)

%dw 2.0
output application/xml
type UScurrency = String {format:"###,###.00"}


fun formatcurrency(region,price)=
	if (region == "US")
	{
		amountUSFormatted: price as Number as UScurrency
	}

	else
	{
		amountEUFormatted: price as Number as UScurrency replace "," with "/" replace "." with "," replace "/" with "."
	}	


fun createNewTransactions(transactions) =
	transactions map ((transaction) ->
		//Assign a local variable within the map scope by utilizing the "using" operator
		using (account = (vars.accountsoutput filter ($.accountID == transaction.customerRef))[0])
		(
			 transaction @(transID: transaction.transactionID, amount: transaction.amount as Number):
				{
					idCustomer: transaction.customerRef,
					nameCustomer: account.name,
					accountType: account.accountType,
					region: transaction.region,

			    } 	++
			    formatcurrency(transaction.region,transaction.amount)    
		)
	)
---
flights:  
{(

		vars.flights map((flightobject,index) ->
			using(localflightID = (upper(flightobject.airlineName) ++ upper(flightobject.flightCode) ++ flightobject.departureDate as Date {format:"yyyy/M/dd"} as String {formaat:"yyyy-MM-dd"}))
			using (filteredTransactions = (vars.transaction filter ($.flightID == localflightID )))
		(

			    flight @(flightCode:flightobject.flightCode):
			    {
			    airline: flightobject.airlineName,
			    departureDate: flightobject.departureDate as Date {format: "yyyy/MM/dd"} as String {format :"yyyy-MMM-dd"},
			    emptySeats:flightobject.availableSeats,
			    fromAirportCode: flightobject.origination,
			    planeType: flightobject.planeType,
			    price: flightobject.price,
			    toAirportCode: flightobject.destination,
				transactions:{(createNewTransactions(filteredTransactions))}
		}
		)	
		)		
)}

